package com.aurienpro.test;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = (int)10.5;
		System.out.println(a);
		double b = 10.5;
		System.out.println(b);
		
		byte i =10;
		byte j = (byte) (i+10);
		System.out.println(j);
	}
	

}
